mapper.select:
select  *     
from ehr_person_file where ehr_person_file.is_deleted=0

	and ehr_person_file.person_id=#{personId,jdbcType=BIGINT}

mapper.update:
		update ehr_person_file set     
            is_deleted=1  
        where is_deleted=0 and ehr_person_file.from=1 and  person_id in

        	 #{item.personId,jdbcType=BIGINT}    

        and file_type in

        	 #{item.fileType,jdbcType=BIGINT}
