mapper.update:

update ehr_argument_info
set ARGUMENT_KEY = #{argumentKey,jdbcType=VARCHAR},
  ARGUMENT_VALUE = #{argumentValue,jdbcType=VARCHAR},
  COMPANY_ID = #{companyId,jdbcType=BIGINT},
  REMARK = #{remark,jdbcType=VARCHAR}
where ID = #{id,jdbcType=BIGINT}

mapper.select:
select
from ehr_argument_info where COMPANY_ID = #{companyId,jdbcType=BIGINT}
AND( instr( ID, #{search,jdbcType=VARCHAR} ) > 0 OR ARGUMENT_KEY like CONCAT(CONCAT('%', #{search, jdbcType=VARCHAR}), '%') OR ARGUMENT_VALUE like CONCAT(CONCAT('%', #{search, jdbcType=VARCHAR}), '%') OR REMARK like CONCAT(CONCAT('%', #{search, jdbcType=VARCHAR}), '%'))
AND ( ARGUMENT_KEY like CONCAT(CONCAT('%', #{search, jdbcType=VARCHAR}), '%') OR ARGUMENT_VALUE like CONCAT(CONCAT('%', #{search, jdbcType=VARCHAR}), '%') OR REMARK like CONCAT(CONCAT('%', #{search, jdbcType=VARCHAR}), '%'))
order by ${order}
desc

select count(*) from ehr_argument_info where COMPANY_ID = #{companyId,jdbcType=BIGINT}
AND( instr( ID, #{search,jdbcType=VARCHAR} ) > 0 OR ARGUMENT_KEY like CONCAT(CONCAT('%', #{search, jdbcType=VARCHAR}), '%') OR ARGUMENT_VALUE like CONCAT(CONCAT('%', #{search, jdbcType=VARCHAR}), '%') OR REMARK like CONCAT(CONCAT('%', #{search, jdbcType=VARCHAR}), '%'))
AND ( ARGUMENT_KEY like CONCAT(CONCAT('%', #{search, jdbcType=VARCHAR}), '%') OR ARGUMENT_VALUE like CONCAT(CONCAT('%', #{search, jdbcType=VARCHAR}), '%') OR REMARK like CONCAT(CONCAT('%', #{search, jdbcType=VARCHAR}), '%'))
