mapper.select:
	select r.*,cd.common_data_name report_type_name 
	from ehr_report r left join ehr_common_data cd on r.report_type=cd.common_data_id
	where r.company_id=#{companyId,jdbcType=BIGINT}

  	and r.report_type = #{reportType,jdbcType=VARCHAR}


  	and r.report_name like CONCAT('%',#{reportName,jdbcType=VARCHAR},'%')


		and r.report_name like CONCAT('%',#{search,jdbcType=VARCHAR},'%')


  	and r.scope_type = #{scopeType,jdbcType=VARCHAR}


  	and r.scope_id = #{scopeId,jdbcType=VARCHAR}

select * from ehr_report where company_id=#{companyId,jdbcType=BIGINT}
	and report_type = #{reportType,jdbcType=VARCHAR}
	and report_name = #{reportName,jdbcType=VARCHAR}

select * from ehr_report 
where (company_id=#{companyId,jdbcType=BIGINT} or scope_type=1 or (scope_type=2 and scope_id=#{companyId,jdbcType=BIGINT}))
	and report_type = #{reportType,jdbcType=VARCHAR}
