mapper.insert:

insert into ehr_salary (salary_id, salary_template_id, person_id, 
  status, salary_memo, usage_flag
  )
values (#{salaryId,jdbcType=BIGINT}, #{salaryTemplateId,jdbcType=BIGINT}, #{personId,jdbcType=BIGINT}, 
  #{status,jdbcType=INTEGER}, #{salaryMemo,jdbcType=VARCHAR}, #{usageFlag,jdbcType=INTEGER}
  )

mapper.select:
select s.*,p.employee_name,p.job_number,p.cert_id,t.template_name from ehr_salary s left join ehr_person p on s.person_id=p.person_id left join ehr_salary_template t on s.salary_template_id=t.salary_template_id where p.company_id=#{companyId,jdbcType=BIGINT}
and p.job_number like CONCAT('%',#{jobNumber,jdbcType=VARCHAR},'%')
and p.employee_name like CONCAT('%',#{employeeName,jdbcType=VARCHAR},'%')
and s.status=#{status,jdbcType=VARCHAR}
and (p.job_number like CONCAT('%',#{search,jdbcType=VARCHAR},'%') or p.employee_name like CONCAT('%',#{search,jdbcType=VARCHAR},'%') or t.template_name like CONCAT('%',#{search,jdbcType=VARCHAR},'%') )
and p.organization_id in #{org.organizationId}

select s.* from ehr_salary s left join ehr_person p on s.person_id=p.person_id
where p.company_id=#{companyId,jdbcType=BIGINT} 
	and s.person_id = #{personId,jdbcType=BIGINT} and s.usage_flag = #{usageFlag,jdbcType=INTEGER}

mapper.delete:
delete from ehr_salary 
where salary_id=#{salaryId,jdbcType=BIGINT} 
	and person_id in (select person_id from ehr_person where company_id=#{companyId,jdbcType=BIGINT})
