select distinct bpd.sb_group_detail_id, gd.prod_id, gd.prod_name from ss_payment_bill_person_detail bpd left join sb_group_detail gd on bpd.sb_group_detail_id=gd.id left join ss_payment_bill_person bp on bp.id=bpd.ss_payment_bill_person_id left join ss_payment_bill b on bp.ss_payment_bill_id=b.id where bp.person_id=#{personId,jdbcType=BIGINT} and b.status <> 3
and b.month >= #{startMonth}
and b.month <= #{endMonth}
