mapper.select:
    select fieldDef.* from  
    ehr_table_field_def fieldDef
	LEFT JOIN ehr_table_def def on def.table_def_id=fieldDef.table_def_id

	where def.is_deleted=0  

	and def.company_id=#{companyId,jdbcType=BIGINT} 



	  and def.key_table=#{keyTable,jdbcType=VARCHAR} 

	  and def.key_field=#{keyField,jdbcType=VARCHAR}

select fieldDef.* from ehr_table_field_def fieldDef left join ehr_table_def def on fieldDef.table_def_id = def.table_def_id
where def.is_deleted=0
and def.company_id=#{companyId,jdbcType=BIGINT}
