mapper.select:
select 

from ehr_city

1=1

   AND  CITY_CODE = #{cityCode,jdbcType=DECIMAL}


   AND   CITY_NAME = #{cityName,jdbcType=VARCHAR}


   AND   CITY_ENGLISH_NAME = #{cityEnglishName,jdbcType=VARCHAR}


   AND   GOVERNING_CITY = #{governingCity,jdbcType=BIGINT}


   AND   AREA = #{area,jdbcType=DECIMAL}


   AND   PROVINCE_ID = #{provinceId,jdbcType=BIGINT}


   AND   PROVINCE_NAME = #{provinceName,jdbcType=VARCHAR}


   AND   IS_USED = #{isUsed,jdbcType=INTEGER}


   AND   CITY_FACTOR = #{cityFactor,jdbcType=DECIMAL}


   AND   AGENT_LEVEL = #{agentLevel,jdbcType=DECIMAL}


   AND   AGENT_DISCOUNT_FACTOR = #{agentDiscountFactor,jdbcType=DECIMAL}


   AND   PINYIN_CODE = #{pinyinCode,jdbcType=VARCHAR}


   AND   IS_DELETED = #{isDeleted,jdbcType=INTEGER}


   AND   CREATE_BY = #{createBy,jdbcType=BIGINT}


   AND   CREATE_DT = #{createDt,jdbcType=TIMESTAMP}


   AND   UPDATE_BY = #{updateBy,jdbcType=BIGINT}


   AND   UPDATE_DT = #{updateDt,jdbcType=TIMESTAMP}


   AND   MIMIC_BY = #{mimicBy,jdbcType=BIGINT}


   AND   PROXY_BY = #{proxyBy,jdbcType=BIGINT}


   AND   SUBTYPE_ID = #{subtypeId,jdbcType=DECIMAL}


   AND   ORIGINAL_CITY_ID = #{originalCityId,jdbcType=BIGINT}
